interface Garbage {
    val collectible: Boolean
}

data class Plastic(override val collectible: Boolean = true) : Garbage
data class Green(override val collectible: Boolean = true) : Garbage
data class Paper(override val collectible: Boolean = false) : Garbage
data class Glass(override val collectible: Boolean = true) : Garbage
data class Chemicals(override val collectible: Boolean = false) : Garbage
data class Oil(override val collectible: Boolean = false) : Garbage
data class Batteries(override val collectible: Boolean = true) : Garbage

import org.junit.Assert.assertEquals
import org.junit.Test
import GarbageCollector as GC

class Test {
    @Test
    fun garbageTest() {

        GC().apply {
            collectGarbage(
                    listOf(Green(),
                            Batteries(),
                            Oil(),
                            Plastic(),
                            Glass(),
                            Chemicals(),
                            Paper()
                    ))
        }.run {
            assertEquals("Count of recyclables incorrect", 2, recyclable)
            assertEquals("Count of burnables incorrect", 1, burnables)
            assertEquals("Count of chemicals incorrect", 3, chemicals)
            assertEquals("Count of organic incorrect", 1, organic)
        }

        GC().apply {
            collectGarbage(
                    listOf(
                            Batteries(),
                            Batteries(),
                            Batteries()
                    ))
        }.run {
            assertEquals("Count of recyclables incorrect", 0, recyclable)
            assertEquals("Count of burnables incorrect", 0, burnables)
            assertEquals("Count of chemicals incorrect", 3, chemicals)
            assertEquals("Count of organic incorrect", 0, organic)
        }

        GC().apply {
            collectGarbage(
                    listOf(
                    ))
        }.run {
            assertEquals("Count of recyclables incorrect", 0, recyclable)
            assertEquals("Count of burnables incorrect", 0, burnables)
            assertEquals("Count of chemicals incorrect", 0, chemicals)
            assertEquals("Count of organic incorrect", 0, organic)
        }
    }


}

class Client( val personalInfo: PersonalInfo? )

class PersonalInfo( val email: String? )

interface Mailer {
    fun sendMessage(email: String, message: String)
}


fun sendMessageToClient(
        client: Client?, message: String?, mailer: Mailer
){
    if (client == null || message == null) return

    val personalInfo = client.personalInfo
    if (personalInfo == null) return


    val email = personalInfo.email
    if( email == null ) return

    mailer.sendMessage(email, message)
}

import org.junit.Test
import org.junit.Assert

class TestNullableTypes {
    fun testSendMessageToClient(
            client: Client?,
            message: String?,
            expectedEmail: String? = null,
            shouldBeInvoked: Boolean = false
    ) {
        var invoked = false
        val expectedMessage = message
        sendMessageToClient(client, message, object : Mailer {
            override fun sendMessage(email: String, message: String) {
                invoked = true
                Assert.assertEquals("The message is not as expected:",
                        expectedMessage, message)
                Assert.assertEquals("The email is not as expected:",
                        expectedEmail, email)
            }
        })
        Assert.assertEquals("The function 'sendMessage' should${if (shouldBeInvoked) "" else "n't"} be invoked",
                shouldBeInvoked, invoked)
    }

    @Test fun everythingIsOk() {
        testSendMessageToClient(Client(PersonalInfo("bob@gmail.com")),
                "Hi Bob! We have an awesome proposition for you...",
                "bob@gmail.com",
                true)
    }

    @Test fun noMessage() {
        testSendMessageToClient(Client(PersonalInfo("bob@gmail.com")), null)
    }

    @Test fun noEmail() {
        testSendMessageToClient(Client(PersonalInfo(null)), "Hi Bob! We have an awesome proposition for you...")
    }

    @Test fun noPersonalInfo() {
        testSendMessageToClient(Client(null), "Hi Bob! We have an awesome proposition for you...")
    }

    @Test fun noClient() {
        testSendMessageToClient(null, "Hi Bob! We have an awesome proposition for you...")
    }
}

